generated.css:
a {
  text-decoration: none;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

ul,
ol {
  list-style-type: none;
}

.item {
  border-bottom: 1px dashed #ccc;
  padding-bottom: 10px;
  padding-top: 10px;
  font-size: 14px;
  text-align: left;
}

.item a {
  display: block;
  display: flex;
}

.item .cover,
.item .cover img {
  width: 70px;
}

.item .detail {
  flex: 1;
  padding-left: 10px;
  font-size: 12px;
}

.item .detail h2 {
  font-size: 16px;
  color: #369;
  display: inline-block;
}

.item .detail h2:hover {
  color: #fff;
  background: #37a;
}

.item .detail .extra {
  color: #666666;
  margin-top: 4px;
}

.item .detail .extra .score {
  color: #FF5722;
}


body main {
  height: calc(100vh - 50px);
  text-align: center;
  padding: 10px;
}

body main section {
  height: 100%;
  overflow: scroll;
  -webkit-overflow-scrolling: touch; /*ios 上增加弹性*/
}

body footer {
  border-top: 1px solid #49A36C;
  height: 50px;
}

body footer ul {
  display: flex;
  flex-direction: row;
  height: 100%;
  justify-content: space-around;
  align-items: center;
}

body footer ul li {
  color: #8A96A7;
}

body footer ul li.active {
  color: #49A36C;
}

body footer ul li span {
  display: flex;
  justify-content: center;
  font-size: 14px;
}

body footer ul li {
  flex-grow:1;
}
body main .loading {
  display: none;
  font-size: 20px;
  animation: 1s rotate linear infinite;
}
body main .loading.a {
  display: inline-block;
}

@keyframes rotate {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}



.searchWrap {
  display: flex;
  justify-content: center;
}

.searchWrap input[type=search] {
  padding: 8px;
  background: #EEF4EE;
  width: calc(100vw - 50px);
  border: none;
}
.searchWrap input[type=search]:focus{
  outline: none;
}

.searchWrap span.btn {
  width: 50px;
  background: #4E9348;
  color: #fff;
  font-size: 14px;
  padding: 6px 8px;
  margin-left: 10px;
  border-radius: 5px;
}





.target {
  display: inline-block;
  padding: 10px 20px;
  border-radius: 4px;
  position: fixed;
  right: 10px;
  bottom: 10px;
  margin: 30px;
  box-shadow: 0 0 3px 1px rgba(0, 0, 0, 0.3);
  cursor: pointer;
  display: none;
}
